import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  title = 'GymBro';
  exercises: any[] = [];
  selectedExercise: any;

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    this.http.get('/assets/json/exercises.json').subscribe((data: any) => {
      this.exercises = data.exercises;
      this.selectedExercise = this.exercises[0];
    });
  }

  onSelect(exercise: any): void {
    this.selectedExercise = exercise;
  }
}
<nav>
  <a routerLink="/create-workout">Create Workout</a>
  <a routerLink="/track-workout">Track Wdorkout</a>
  <a routerLink="/reminders">Reminders</a>
  <a routerLink="/tips">Tips</a>
</nav>
<router-outlet></router-outlet>
